import React from "react";
import "./Operadores.css";

function Operadores({setPrimerNumero, setSegundoNumero, valueInput, setValueInput, primerNumero, segundoNumero}) {

    const limpiarCache = () => {
        setValueInput('');
        setPrimerNumero('');
    }

    const limpiarInput = () => {
        setValueInput('');
    };

    const operar = () => {
        setValueInput(Number(primerNumero)+Number(valueInput));
    };

    const capturarPrimerNumero = (op) => {
        if(
            op === '+' ||
            op === '-' ||
            op === 'x' ||
            op === '/'
        ){
            setPrimerNumero((valueInput).toString());
        };

        limpiarInput();
    };

    return (
        <div className="contain-operadores">
            <div><button
            onClick={() => capturarPrimerNumero('+')}
            >+</button></div>
            <div><button
            onClick={() => capturarPrimerNumero('-')}
            >-</button></div>
            <div><button
            onClick={() => capturarPrimerNumero('x')}
            >x</button></div>
            <div><button
            onClick={() => capturarPrimerNumero('/')}
            >/</button></div>
            <div><button
            onClick={limpiarCache}
            >CE</button></div>
            <div><button
            onClick={operar}
            >=</button></div>
        </div>
    );
}

export { Operadores };
