import React from "react";
import "./Operadores.css";

function Operadores({setPrimerNumero, setSegundoNumero, valueInput, setValueInput, primerNumero, segundoNumero}) {

    const limpiar = () => {
        setValueInput('');
    };

    const operar = () => {
        alert(primerNumero);
        alert(segundoNumero);
        setSegundoNumero(valueInput);
        setValueInput(parseInt(primerNumero)+parseInt(segundoNumero));
    };

    const capturarPrimerNumero = (op) => {
        if(
            op === '+' ||
            op === '-' ||
            op === 'x' ||
            op === '/'
        ){
            setPrimerNumero(valueInput);
        };

        limpiar();
    };

    return (
        <div className="contain-operadores">
            <div><button
            onClick={() => capturarPrimerNumero('+')}
            >+</button></div>
            <div><button
            onClick={() => capturarPrimerNumero('-')}
            >-</button></div>
            <div><button
            onClick={() => capturarPrimerNumero('x')}
            >x</button></div>
            <div><button
            onClick={() => capturarPrimerNumero('/')}
            >/</button></div>
            <div><button
            onClick={limpiar}
            >CE</button></div>
            <div><button
            onClick={operar}
            >=</button></div>
        </div>
    );
}

export { Operadores };
