import React from "react";
import "./Operadores.css";

function Operadores({setPrimerNumero, setSegundoNumero, valueInput}) {

    const limpiar = () => {
        
    }

    const operar = (op) => {
        if(
            op === '+' ||
            op === '-' ||
            op === 'x' ||
            op === '/'
        ){
            setPrimerNumero(valueInput);
        }
    }

    return (
        <div className="contain-operadores">
            <div><button
            onClick={() => operar('+')}
            >+</button></div>
            <div><button
            onClick={() => operar('-')}
            >-</button></div>
            <div><button
            onClick={() => operar('x')}
            >x</button></div>
            <div><button
            onClick={() => operar('/')}
            >/</button></div>
            <div><button
            onClick={limpiar}
            >CE</button></div>
            <div><button
            onClick={() => operar('=')}
            >=</button></div>
        </div>
    );
}

export { Operadores };
